CREATE OR REPLACE FUNCTION pls_obter_se_copartic_mat_lib ( nr_seq_tipo_copart_p bigint, cd_material_p bigint, nr_seq_estrut_mat_p bigint, nr_seq_material_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(10) := '';
nr_seq_estrutura_w	bigint;
nr_seq_material_w	bigint;
cd_material_ops_w	bigint;
cd_material_w		bigint;

C01 CURSOR FOR
	SELECT	b.ie_liberado
	from	pls_tipo_coparticipacao a,
		pls_coparticipacao_mat b
	where	a.nr_sequencia					= b.nr_seq_tipo_coparticipacao
	and	a.nr_sequencia					= nr_seq_tipo_copart_p
	and	coalesce(b.cd_material, cd_material_w)		= cd_material_w
	and 	coalesce(b.nr_seq_estrut_mat, nr_seq_estrutura_w)	= nr_seq_estrutura_w
	and	coalesce(b.nr_seq_material, nr_seq_material_w)	= nr_seq_material_w
	order by	coalesce(nr_seq_material,0),
			coalesce(nr_seq_estrut_mat,0);

BEGIN
cd_material_w		:= coalesce(cd_material_p, 0);
nr_seq_estrutura_w 	:= coalesce(nr_seq_estrut_mat_p,0);
nr_seq_material_w 	:= coalesce(nr_seq_material_p,0);

ds_retorno_w	:= 'N';

open C01;
loop
fetch C01 into
	ds_retorno_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
end loop;
close C01;

return ds_retorno_w;

end;
