CREATE OR REPLACE FUNCTION pls_obter_se_copartic ( cd_guia_p pls_conta.cd_guia%type, nr_seq_segurado_p pls_conta.nr_seq_segurado%type, nr_seq_conta_proc_p pls_conta_proc.nr_sequencia%type, nr_seq_conta_mat_p pls_conta_mat.nr_sequencia%type) RETURNS varchar AS $body$
DECLARE

/* Function criada para a verificação de coparticipações já realizadas para a guia de internação */

ie_copartic_gerada_w		varchar(1);
qt_copartic_w			bigint;
qt_copartic_ww			bigint;


BEGIN

select	count(1)
into STRICT	qt_copartic_w
from	pls_conta a,
	pls_conta_coparticipacao b
where	a.nr_sequencia		= b.nr_seq_conta
and	a.cd_guia_ok		= cd_guia_p
and	a.nr_seq_segurado	= nr_seq_segurado_p
and	((b.nr_seq_conta_proc	!= nr_seq_conta_proc_p) or (coalesce(b.nr_seq_conta_proc::text, '') = ''))
and	((b.nr_seq_conta_mat	!= nr_seq_conta_mat_p) 	or (coalesce(b.nr_seq_conta_mat::text, '') = ''))
and	a.ie_tipo_guia		in ('5','6'); -- Somente as guias de internação
qt_copartic_w	:= qt_copartic_w;

if (qt_copartic_w > 0) then
	ie_copartic_gerada_w	:= 'S';
else
	ie_copartic_gerada_w	:= 'N';
end if;

return	ie_copartic_gerada_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
