SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_se_cotacao_final ( nr_cotacao_p bigint) RETURNS varchar AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Verificar se todos os itens da cotação estão finalizados
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [ ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:Performance.
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
ie_todos_finalizados_w		varchar(1);
qt_total_itens_w		integer;
qt_itens_finalizados_w		integer;
qt_retorno_w			integer;


BEGIN

select	count(1)
into STRICT	qt_total_itens_w
from	pls_item_cotacao
where	nr_seq_cotacao		= nr_cotacao_p;

select	count(1)
into STRICT	qt_itens_finalizados_w
from	pls_item_cotacao
where	nr_seq_cotacao		= nr_cotacao_p
and	ie_status		= 4;

select	count(1)
into STRICT	qt_retorno_w
from	pls_retorno_cotacao
where	nr_seq_cotacao		= nr_cotacao_p;

if (qt_total_itens_w = qt_itens_finalizados_w) and (qt_retorno_w > 0) then
	ie_todos_finalizados_w	:= 'S';
else
	ie_todos_finalizados_w	:= 'N';
end if;

return ie_todos_finalizados_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_se_cotacao_final ( nr_cotacao_p bigint) FROM PUBLIC;

