CREATE OR REPLACE FUNCTION pls_obter_se_credenciado_prest ( cd_pessoa_fisica_p text, nr_seq_prestador_p bigint ) RETURNS varchar AS $body$
DECLARE


qt_credenciado_w	integer	:= 0;
ie_credenciado_w	varchar(1)	:= 'N';


BEGIN
/*Verifica se o médico é um prestador credenciado*/

select	count(*)
into STRICT	qt_credenciado_w
from	pls_prestador
where	cd_pessoa_fisica	= cd_pessoa_fisica_p
and	ie_tipo_vinculo		= 'C';

if (qt_credenciado_w	= 0) then
	/*Ou se é credenciado ao prestador.*/

	select	count(b.nr_sequencia)
	into STRICT	qt_credenciado_w
	from	pls_prestador_medico a,
		pls_prestador b
	where	a.nr_seq_prestador	= b.nr_sequencia
	and	a.cd_medico 		= cd_pessoa_fisica_p
	and	b.nr_sequencia		= nr_seq_prestador_p
	and	a.ie_situacao		= 'A'
	and	trunc(clock_timestamp(),'dd') between trunc(coalesce(a.dt_inclusao,clock_timestamp()),'dd') and  fim_dia(coalesce(a.dt_exclusao,clock_timestamp()))
	order by b.nr_sequencia;
end if;

if (qt_credenciado_w	> 0) then
	ie_credenciado_w	:= 'S';
end if;


return	ie_credenciado_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
