CREATE OR REPLACE FUNCTION pls_obter_se_cred_prestador ( nr_seq_prestador_exec_p bigint, nr_seq_prestador_atend_p bigint, dt_referencia_p timestamp, cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE


qt_credenciado_w		integer	:= 0;
ie_credenciado_w		varchar(1)	:= 'N';
qt_credenciado_periodo_w	bigint;
nr_seq_prestador_w		bigint;
qt_prest_pj_w			bigint;
nr_seq_hist_vinc_w		bigint;
ie_tipo_vinculo_w		varchar(1);
ie_tipo_vinculo_ww		varchar(1);


BEGIN

select	count(1)
into STRICT	qt_credenciado_w
from	pls_prestador_medico
where	cd_medico		= cd_pessoa_fisica_p
and	ie_tipo_vinculo		= 'C'
and	nr_seq_prestador	= nr_seq_prestador_exec_p;
if (qt_credenciado_w = 0) then
	select	max(a.ie_tipo_historico)
	into STRICT 	ie_tipo_vinculo_w
	from	pls_credenciamento_hist a
	where	a.nr_seq_prestador = nr_seq_prestador_exec_p
	and	a.cd_pessoa_fisica 	= cd_pessoa_fisica_p
	and	trunc(a.dt_historico,'dd') >= fim_dia(dt_referencia_p)
	and	a.dt_historico = (	SELECT min(x.dt_historico)
					from	pls_credenciamento_hist x
					where	x.nr_seq_prestador = nr_seq_prestador_exec_p
					and	x.cd_pessoa_fisica 	= cd_pessoa_fisica_p
					and	trunc(x.dt_historico,'dd') >= fim_dia(dt_referencia_p));
	if (coalesce(ie_tipo_vinculo_w,'C') = 'D') then
		select	count(1)
		into STRICT	qt_credenciado_periodo_w
		from	pls_prestador_medico
		where	cd_medico		= cd_pessoa_fisica_p
		--and	ie_situacao		= 'A'
		and	nr_seq_prestador	= nr_seq_prestador_exec_p
		and	trunc(dt_referencia_p) >= trunc(coalesce(dt_inclusao,dt_referencia_p),'dd')
		and 	trunc(dt_referencia_p) < trunc(coalesce(dt_exclusao,dt_referencia_p+1));
		if (qt_credenciado_periodo_w = 0) then
			ie_credenciado_w 	:= 'N';
		else
			ie_credenciado_w	:= 'S';
			goto final;
		end if;
	end if;
end if;
if (qt_credenciado_w > 0) then

	select	max(a.ie_tipo_historico)
	into STRICT 	ie_tipo_vinculo_w
	from	pls_credenciamento_hist a
	where	a.nr_seq_prestador = nr_seq_prestador_exec_p
	and	a.cd_pessoa_fisica 	= cd_pessoa_fisica_p
	and	trunc(a.dt_historico,'dd') >= fim_dia(dt_referencia_p)
	and	a.dt_historico = (	SELECT max(x.dt_historico)
					from	pls_credenciamento_hist x
					where	x.nr_seq_prestador = nr_seq_prestador_exec_p
					and	x.cd_pessoa_fisica 	= cd_pessoa_fisica_p
					and	trunc(x.dt_historico,'dd') >= fim_dia(dt_referencia_p));

	if (coalesce(ie_tipo_vinculo_w,'C') = 'D') then
		select	count(1)
		into STRICT	qt_credenciado_periodo_w
		from	pls_prestador_medico
		where	cd_medico		= cd_pessoa_fisica_p
		--and	ie_situacao		= 'A'
		and	nr_seq_prestador	= nr_seq_prestador_exec_p
		and	trunc(dt_referencia_p) >= trunc(coalesce(dt_inclusao,dt_referencia_p),'dd')
		and 	trunc(dt_referencia_p) < trunc(coalesce(dt_exclusao,dt_referencia_p+1));

		if (qt_credenciado_periodo_w = 0) then
			ie_credenciado_w 	:= 'N';
		else
			ie_credenciado_w	:= 'S';
		end if;
	else
		select	count(1)
		into STRICT	qt_credenciado_periodo_w
		from	pls_prestador_medico
		where	cd_medico		= cd_pessoa_fisica_p
		and	ie_tipo_vinculo		= 'C'
		and	nr_seq_prestador	= nr_seq_prestador_exec_p
		and	trunc(dt_referencia_p) >= trunc(coalesce(dt_inclusao,dt_referencia_p),'dd')
		and 	trunc(dt_referencia_p) < trunc(coalesce(dt_exclusao,dt_referencia_p+1));

		if (qt_credenciado_periodo_w = 0) then
			ie_credenciado_w 	:= 'N';
		else
			ie_credenciado_w	:= 'S';
		end if;
	end if;
else

	select	count(1)
	into STRICT	qt_credenciado_w
	from	pls_prestador_medico
	where	cd_medico		= cd_pessoa_fisica_p
	and	ie_tipo_vinculo		= 'C'
	and	nr_seq_prestador	= nr_seq_prestador_atend_p;
	if (qt_credenciado_w > 0) then

		select	max(a.ie_tipo_historico)
		into STRICT 	ie_tipo_vinculo_w
		from	pls_credenciamento_hist a
		where	a.nr_seq_prestador = nr_seq_prestador_atend_p
		and	a.cd_pessoa_fisica 	= cd_pessoa_fisica_p
		and	trunc(a.dt_historico,'dd') >= fim_dia(dt_referencia_p)
		and	a.dt_historico = (	SELECT max(x.dt_historico)
						from	pls_credenciamento_hist x
						where	x.nr_seq_prestador = nr_seq_prestador_atend_p
						and	x.cd_pessoa_fisica 	= cd_pessoa_fisica_p
						and	trunc(x.dt_historico,'dd') >= fim_dia(dt_referencia_p));
		if (coalesce(ie_tipo_vinculo_w,'V') = 'D') then
			select	count(1)
			into STRICT	qt_credenciado_periodo_w
			from	pls_prestador_medico
			where	cd_medico		= cd_pessoa_fisica_p
			--and	ie_situacao		= 'A'
			and	nr_seq_prestador	= nr_seq_prestador_atend_p
			and	trunc(dt_referencia_p) >= trunc(coalesce(dt_inclusao,dt_referencia_p),'dd')
			and 	trunc(dt_referencia_p) < trunc(coalesce(dt_exclusao,dt_referencia_p+1));

			if (qt_credenciado_periodo_w = 0) then
				ie_credenciado_w 	:= 'N';
			else
				ie_credenciado_w	:= 'S';
			end if;
		else
			select	count(1)
			into STRICT	qt_credenciado_periodo_w
			from	pls_prestador_medico
			where	cd_medico		= cd_pessoa_fisica_p
			and	ie_tipo_vinculo		= 'C'
			--and	ie_situacao		= 'A'
			and	nr_seq_prestador	= nr_seq_prestador_atend_p
			and	trunc(dt_referencia_p) >= trunc(coalesce(dt_inclusao,dt_referencia_p),'dd')
			and 	trunc(dt_referencia_p) < trunc(coalesce(dt_exclusao,dt_referencia_p+1));

			if (qt_credenciado_periodo_w = 0) then
				ie_credenciado_w 	:= 'N';
			else
				ie_credenciado_w	:= 'S';
			end if;
		end if;
	else
		begin
		select	max(a.nr_sequencia),
			max(a.ie_tipo_vinculo)
		into STRICT	nr_seq_prestador_w,
			ie_tipo_vinculo_ww
		from	pls_prestador	a
		where	a.cd_pessoa_fisica	= cd_pessoa_fisica_p
		and	a.nr_sequencia	in (	SELECT 	max(x.nr_sequencia)-- Tratamento realizado para garantir que o tipo de vinculo selecionado Ã© o mesmo da seq selecionada
						from	pls_prestador	x
						where	x.cd_pessoa_fisica = cd_pessoa_fisica_p);
		exception
		when others then
			nr_seq_prestador_w := 0;
		end;

		if (coalesce(nr_seq_prestador_w,0) > 0) then
			select	max(a.ie_tipo_historico)
			into STRICT 	ie_tipo_vinculo_w
			from	pls_credenciamento_hist a
			where	a.nr_seq_prestador = nr_seq_prestador_w
			and	a.cd_pessoa_fisica 	= cd_pessoa_fisica_p
			and	trunc(a.dt_historico,'dd') >= fim_dia(dt_referencia_p)
			and	a.dt_historico = (	SELECT max(x.dt_historico)
							from	pls_credenciamento_hist x
							where	x.nr_seq_prestador = nr_seq_prestador_w
							and	x.cd_pessoa_fisica 	= cd_pessoa_fisica_p
							and	trunc(x.dt_historico,'dd') >= fim_dia(dt_referencia_p));
			if (coalesce(ie_tipo_vinculo_w,'C') = 'D') then
				select	count(1)
				into STRICT	qt_credenciado_periodo_w
				from	pls_prestador
				where	nr_sequencia	= nr_seq_prestador_w
				and	trunc(dt_referencia_p) >= trunc(coalesce(dt_cadastro,dt_referencia_p),'dd')
				and 	trunc(dt_referencia_p) < trunc(coalesce(dt_exclusao,dt_referencia_p+1));
				if (qt_credenciado_periodo_w = 0) then
					ie_credenciado_w 	:= 'N';
				else
					ie_credenciado_w	:= 'S';
					goto final;
				end if;
			elsif (ie_tipo_vinculo_w = 'C') then
				ie_credenciado_w	:= 'N';
			else
				if (ie_tipo_vinculo_ww = 'C') then
					ie_credenciado_w	:= 'S';
				else
					ie_credenciado_w	:= 'N';
				end if;
			end if;
		else
			ie_credenciado_w	:= 'N';
		end if;
	end if;
end if;

<<final>>

return	ie_credenciado_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
