CREATE OR REPLACE FUNCTION pls_obter_se_data_valida ( ds_data_p text, ds_mascara_p text, ie_tipo_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno	varchar(4000);
dt_valida_w	timestamp;


BEGIN

begin
select	to_date(ds_data_p,ds_mascara_p)
into STRICT	dt_valida_w
;

select	CASE WHEN ie_tipo_p=1 THEN  'Data início: ' || ds_data_p  ELSE 'Data fim: ' || ds_data_p END
into STRICT	ds_retorno
;

exception
when others then
	select	CASE WHEN ie_tipo_p=1 THEN  'Data início: '   ELSE 'Data fim: ' END  || wheb_mensagem_pck.get_texto(308244, 'DT_REFERENCIA='||ds_data_p)
	into STRICT	ds_retorno
	;
end;

return	ds_retorno;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
