CREATE OR REPLACE FUNCTION pls_obter_se_desc_idade_sca ( nr_seq_plano_sca_p bigint, ie_regulamentacao_ant_p text, ie_regulamentacao_nova_p text, nr_seq_segurado_anterior_p bigint) RETURNS varchar AS $body$
DECLARE


ie_desconsiderar_idade_w	varchar(10) := 'N';
ie_benef_ant_possui_sca_w	varchar(10);
ie_possui_vinculo_w		bigint;


BEGIN
select	max(ie_desconsiderar_idade),
	max(ie_benef_ant_possui_sca)
into STRICT	ie_desconsiderar_idade_w,
	ie_benef_ant_possui_sca_w
from	pls_regra_migracao_sca
where	nr_seq_plano			= nr_seq_plano_sca_p
and	((ie_regulamentacao_ant		= ie_regulamentacao_ant_p) or (coalesce(ie_regulamentacao_ant::text, '') = ''))
and	((ie_regulamentacao_nova	= ie_regulamentacao_nova_p) or (coalesce(ie_regulamentacao_nova::text, '') = ''));

--Só desconsidera a idade se o benefiário anterior possuir SCA.  OS 867768
if (coalesce(ie_benef_ant_possui_sca_w,'N') = 'S') then
	select 	count(1)
	into STRICT	ie_possui_vinculo_w
	from 	pls_sca_vinculo
	where 	nr_seq_plano = nr_seq_plano_sca_p
	and 	nr_seq_segurado = nr_seq_segurado_anterior_p;

	if (ie_possui_vinculo_w = 0) then
		ie_desconsiderar_idade_w	:= 'N';
	else
		ie_desconsiderar_idade_w	:= coalesce(ie_desconsiderar_idade_w,'N');
	end if;
else
	ie_desconsiderar_idade_w	:= coalesce(ie_desconsiderar_idade_w,'N');
end if;

return	ie_desconsiderar_idade_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
