CREATE OR REPLACE FUNCTION pls_obter_se_email_enviad_mens (nr_seq_mensalidade_p pls_mensalidade.nr_sequencia%type) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);
qt_email_enviado_w	bigint;


BEGIN

select	count(1)
into STRICT	qt_email_enviado_w
from 	pls_mensalidade_envio
where 	nr_seq_mensalidade = nr_seq_mensalidade_p
and	coalesce(ie_situacao_envio, 'E') = 'E';

if (qt_email_enviado_w = 0) then
	select	count(1)
	into STRICT	qt_email_enviado_w
	from	pls_email
	where	nr_seq_mensalidade = nr_seq_mensalidade_p
	and	ie_status in ('E', 'EP', 'P');

	if (qt_email_enviado_w > 0) then
		ds_retorno_w	:= 'S';
	else
		ds_retorno_w	:= 'N';
	end if;
else
	ds_retorno_w	:= 'S';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
