SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_se_envio_scs ( nr_seq_requisicao_p bigint, nr_seq_guia_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(2);
qt_proc_lib_w			smallint;
qt_proc_envia_w			smallint;


BEGIN

if (nr_seq_requisicao_p IS NOT NULL AND nr_seq_requisicao_p::text <> '') then
	select	count(*)
	into STRICT	qt_proc_lib_w
	from	pls_requisicao_proc
	where	nr_seq_requisicao	= nr_seq_requisicao_p
	and	ie_status		= 'S';

	select	count(*)
	into STRICT	qt_proc_envia_w
	from	pls_requisicao_proc
	where	nr_seq_requisicao	= nr_seq_requisicao_p
	and	ie_status		<> 'S';
elsif (nr_seq_guia_p IS NOT NULL AND nr_seq_guia_p::text <> '') then
	select	count(1)
	into STRICT	qt_proc_lib_w
	from	pls_guia_plano_proc
	where	nr_seq_guia			= nr_seq_guia_p
	and	coalesce(ie_libera_intercambio,'N')	= 'S';

	select	count(1)
	into STRICT	qt_proc_envia_w
	from	pls_guia_plano_proc
	where	nr_seq_guia			= nr_seq_guia_p
	and	coalesce(ie_libera_intercambio,'N')	<> 'S';
end if;

if (qt_proc_lib_w	> 0)	and (qt_proc_envia_w	= 0)  then
	ds_retorno_w	:= 'N';
else
	ds_retorno_w	:= 'S';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
