CREATE OR REPLACE FUNCTION pls_obter_se_env_scs_benef_rep ( nr_seq_segurado_p bigint) RETURNS varchar AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Obtém se há repasse..
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[ x]  Objetos do dicionário [ ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:Performance.
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
ie_retorno_w			varchar(1)	:= 'N';
ie_tipo_repasse_w		varchar(2);
ie_solic_autor_origem_w		varchar(2);

nr_seq_intercambio_w		bigint;

qt_repasse_w			bigint;
qt_regra_repasse_w		bigint;
qt_regra_excec_w		bigint;

ie_tipo_segurado_w		varchar(3);


BEGIN

/*select	count(1)
into	qt_repasse_w
from	pls_segurado_repasse
where	nr_seq_segurado	= nr_seq_segurado_p
and	sysdate	between dt_repasse and fim_dia(nvl(dt_fim_repasse,sysdate))
and	dt_liberacao	is not null;*/
select	ie_tipo_repasse,
	ie_tipo_segurado
into STRICT	ie_tipo_repasse_w,
	ie_tipo_segurado_w
from	pls_segurado
where	nr_sequencia = nr_seq_segurado_p;

select	count(1)
into STRICT	qt_regra_repasse_w
from	pls_regra_intercambio_aut;

if (ie_tipo_repasse_w IS NOT NULL AND ie_tipo_repasse_w::text <> '') and (ie_tipo_segurado_w in ('T', 'R')) and (qt_regra_repasse_w	> 0) then
	/*begin
		select	ie_tipo_repasse
		into	ie_tipo_repasse_w
		from	pls_segurado_repasse
		where	nr_seq_segurado	= nr_seq_segurado_p;
	exception
	when others then
		ie_tipo_repasse_w	:= 'X';
	end;*/
	if (coalesce(ie_tipo_repasse_w,'X')	<> 'X') then
		begin
			select	ie_solic_autor_origem
			into STRICT	ie_solic_autor_origem_w
			from	pls_regra_intercambio_aut
			where	((coalesce(ie_tipo_repasse::text, '') = '')
			or (ie_tipo_repasse	= ie_tipo_repasse_w));
		exception
		when others then
			ie_solic_autor_origem_w	:= 'N';
		end;

		begin
			select	nr_seq_intercambio
			into STRICT	nr_seq_intercambio_w
			from	pls_segurado
			where	nr_sequencia	= nr_seq_segurado_p;
		exception
		when others then
			nr_seq_intercambio_w	:= 0;
		end;

		if (coalesce(ie_solic_autor_origem_w,'N')	= 'S') then
			select	count(1)
			into STRICT	qt_regra_excec_w
			from	pls_excecao_interc_aut
			where (coalesce(nr_seq_contrato_int::text, '') = '' or nr_seq_contrato_int	= nr_seq_intercambio_w)
			and	ie_situacao		= 'A';

			if (coalesce(qt_regra_excec_w,0)	> 0) then
				ie_retorno_w	:= 'N';
			else
				ie_retorno_w	:= 'S';
			end if;
		end if;
	end if;
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
