CREATE OR REPLACE FUNCTION pls_obter_se_escala_vend ( nr_seq_regra_p bigint, nr_seq_origem_solic_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1);
qt_retorno_w		bigint;


BEGIN

ds_retorno_w	:= 'S';

if (coalesce(nr_seq_origem_solic_p::text, '') = '') then
	ds_retorno_w	:= 'N';
else
	select	count(*)
	into STRICT	qt_retorno_w
	FROM pls_solic_regra_escala
WHERE nr_seq_origem_solic	<> nr_seq_origem_solic_p;

	/*Verificar se na regra a origem da solicitcatção*/

	if (qt_retorno_w	= 0) then
		ds_retorno_w	:= 'N';
	elsif (qt_retorno_w	> 0) then
		/*Caso existir algum registro verificar se pertecem a essa regra*/

		select	count(*)
		into STRICT	qt_retorno_w
		FROM pls_solic_regra_escala
WHERE nr_seq_origem_solic	= nr_seq_origem_solic_p;

		if (qt_retorno_w	= 0) then
			ds_retorno_w	:= 'N';
		end if;
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
