CREATE OR REPLACE FUNCTION pls_obter_se_espec_cbo ( nr_seq_cbo_conta_p bigint, nr_seq_prestador_p bigint) RETURNS varchar AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[ X ]  Objetos do dicionário [  ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
-------------------------------------------------------------------------------------------------------------------
Referências:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
ie_espec_validos_w		varchar(1)	:= 'N';
qt_espec_valida_w		bigint;

/*Obter as especialidades com deteminado CBO*/

C01 CURSOR FOR
	SELECT	cd_especialidade
	from	especialidade_medica
	where	nr_seq_cbo_saude = nr_seq_cbo_conta_p;


BEGIN
for r_c01 in C01 loop
	select	count(1)
	into STRICT	qt_espec_valida_w
	from	pls_prestador_med_espec	a
	where	a.cd_especialidade	= r_c01.cd_especialidade
	and 	a.nr_seq_prestador	= nr_seq_prestador_p
	and	clock_timestamp() between trunc(coalesce(a.dt_inicio_vigencia, clock_timestamp())) and fim_dia(coalesce(a.dt_fim_vigencia, clock_timestamp()));

	if (qt_espec_valida_w > 0)	then
		ie_espec_validos_w	:= 'S';
		goto final;
	end if;
end loop;

<<final>>

return	ie_espec_validos_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
