CREATE OR REPLACE FUNCTION pls_obter_se_espec_med_unica ( nr_seq_prestador_p bigint, cd_medico_solicitante_p text) RETURNS bigint AS $body$
DECLARE


qt_registros_w			bigint;
cd_especialidade_w		integer;


BEGIN
select	count(*)
into STRICT	qt_registros_w
from	pls_prestador_med_espec
where	nr_seq_prestador	= nr_seq_prestador_p
and	cd_pessoa_fisica	= cd_medico_solicitante_p;

if (qt_registros_w	= 1) then
	select	cd_especialidade
	into STRICT	cd_especialidade_w
	from	pls_prestador_med_espec	a
	where	nr_seq_prestador	= nr_seq_prestador_p
	and	cd_pessoa_fisica	= cd_medico_solicitante_p
	and	clock_timestamp() between trunc(coalesce(a.dt_inicio_vigencia, clock_timestamp())) and fim_dia(coalesce(a.dt_fim_vigencia, clock_timestamp()));
else
	cd_especialidade_w	:= 0;
end if;

return cd_especialidade_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
