CREATE OR REPLACE FUNCTION pls_obter_se_espec_prestador ( nr_seq_prestador_p pls_prestador.nr_sequencia%type, cd_especialidade_p pls_prestador_med_espec.cd_especialidade%type) RETURNS varchar AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Obter se a especialidade está vinculada ao prestador
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  X]  Objetos do dicionário [  ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
-------------------------------------------------------------------------------------------------------------------
Referências:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
ie_retorno_w			varchar(1);
qt_existe_espec_w		integer;


BEGIN
-- Inicializar as variáveis
ie_retorno_w		:= 'N';
qt_existe_espec_w	:= 0;

-- Contar os médicos do prestador que atendam na especialidade sendo verificada
select	count(1)
into STRICT	qt_existe_espec_w
from	pls_prestador_med_espec	a
where	a.nr_seq_prestador	= nr_seq_prestador_p
and	a.cd_especialidade	= cd_especialidade_p
and	clock_timestamp()	between trunc(coalesce(a.dt_inicio_vigencia, clock_timestamp())) and fim_dia(coalesce(a.dt_fim_vigencia, clock_timestamp()))  LIMIT 1;

-- Se existir então o prestador pode atender nesta especialidade
if (qt_existe_espec_w > 0) then
	ie_retorno_w		:= 'S';
end if;

return ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
