CREATE OR REPLACE FUNCTION pls_obter_se_estip_filantropia (nr_seq_contrato_p bigint, dt_referencia_p timestamp) RETURNS varchar AS $body$
DECLARE


ie_filantropia_w	varchar(1)	:= 'N';
cd_cgc_estipulante_w	varchar(14);

c01 CURSOR FOR
SELECT	a.ie_isencao_tributaria
from	pessoa_juridica_inf_trib a
where	a.cd_cgc	= cd_cgc_estipulante_w
and	coalesce(dt_referencia_p,clock_timestamp()) between a.dt_inicio_vigencia and coalesce(a.dt_fim_vigencia,coalesce(dt_referencia_p,clock_timestamp()));


BEGIN

if (nr_seq_contrato_p IS NOT NULL AND nr_seq_contrato_p::text <> '') then
	select	max(cd_cgc_estipulante)
	into STRICT	cd_cgc_estipulante_w
	from	pls_contrato a
	where	a.nr_sequencia	= nr_seq_contrato_p;

	if (cd_cgc_estipulante_w IS NOT NULL AND cd_cgc_estipulante_w::text <> '') then
		open c01;
		loop
		fetch c01 into
			ie_filantropia_w;
		EXIT WHEN NOT FOUND; /* apply on c01 */
			begin
			null;
			end;
		end loop;
		close c01;
	end if;
end if;

return	ie_filantropia_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
