CREATE OR REPLACE FUNCTION pls_obter_se_estip_pagador ( nr_seq_proposta_p bigint, nr_seq_pagador_proposta_p bigint, nr_seq_contrato_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(5);
cd_pf_estipulante_w	varchar(10);
cd_cgc_estipulante_w	varchar(14);
cd_pf_pagador_w		varchar(10);
cd_cgc_pagador_w	varchar(14);
nr_seq_proposta_w	bigint;


BEGIN

if (coalesce(nr_seq_proposta_p,0) <> 0) then
	select	cd_estipulante,
		cd_cgc_estipulante
	into STRICT	cd_pf_estipulante_w,
		cd_cgc_estipulante_w
	from	pls_proposta_adesao
	where	nr_sequencia	= nr_seq_proposta_p;

	begin
	select	'S'
	into STRICT	ds_retorno_w
	
	where	exists (SELECT	1
			from	pls_proposta_pagador
			where (cd_pagador	= cd_pf_estipulante_w
			or	cd_cgc_pagador	= cd_cgc_estipulante_w)
			and	nr_seq_proposta	= nr_seq_proposta_p);
	exception
	when others then
		ds_retorno_w	:= 'N';
	end;
elsif (coalesce(nr_seq_pagador_proposta_p,0) <> 0) then
	select	cd_pagador,
		cd_cgc_pagador,
		nr_seq_proposta
	into STRICT	cd_pf_pagador_w,
		cd_cgc_pagador_w,
		nr_seq_proposta_w
	from	pls_proposta_pagador
	where	nr_sequencia	= nr_seq_pagador_proposta_p;

	begin
	select	'S'
	into STRICT	ds_retorno_w
	
	where	exists (SELECT	1
			from	pls_proposta_adesao
			where (cd_estipulante		= cd_pf_pagador_w
			or	cd_cgc_estipulante	= cd_cgc_pagador_w)
			and	nr_sequencia		= nr_seq_proposta_w);
	exception
	when others then
		ds_retorno_w	:= 'N';
	end;

elsif (coalesce(nr_seq_contrato_p,0) <> 0) then
	select	cd_pf_estipulante,
		cd_cgc_estipulante
	into STRICT	cd_pf_estipulante_w,
		cd_cgc_estipulante_w
	from	pls_contrato
	where	nr_sequencia	= nr_seq_contrato_p;

	begin
	select	'S'
	into STRICT	ds_retorno_w
	
	where	exists (SELECT	1
			from	pls_contrato_pagador
			where (cd_pessoa_fisica	= cd_pf_estipulante_w
			or	cd_cgc			= cd_cgc_estipulante_w)
			and	nr_seq_contrato		= nr_seq_contrato_p);
	exception
	when others then
		ds_retorno_w	:= 'N';
	end;

end if;


return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
