CREATE OR REPLACE FUNCTION pls_obter_se_estruturas_mat ( nr_seq_estrutura_mat_p bigint, nr_seq_material_p bigint) RETURNS varchar AS $body$
DECLARE

ie_retorno_w			varchar(10)	:= 'N';

BEGIN

begin
select	'S'
into STRICT	ie_retorno_w

where	nr_seq_material_p	in (	SELECT	x.nr_sequencia
				from	pls_material	x
				where	x.nr_seq_estrut_mat in (WITH RECURSIVE cte AS (
select  nr_sequencia x
								from 	pls_estrutura_material x WHERE x.nr_sequencia = nr_seq_estrutura_mat_p
  UNION ALL
select  nr_sequencia x
								from 	pls_estrutura_material x JOIN cte c ON (c.prior nr_sequencia = x.x.nr_seq_superior)

) SELECT * FROM cte;
));
exception
when others then
	ie_retorno_w	:= 'N';
end;

if (coalesce(ie_retorno_w::text, '') = '') then
	ie_retorno_w	:= 'N';
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
