CREATE OR REPLACE FUNCTION pls_obter_se_excecao_hor ( cd_procedimento_p bigint, ie_origem_proced_p bigint, nr_seq_regra_hor_p bigint) RETURNS varchar AS $body$
DECLARE


ie_excecao_w		varchar(1) := 'N';
nr_seq_grupo_serv_w	bigint;
ie_grupo_serv_w		varchar(1);

C01 CURSOR FOR
	SELECT	nr_seq_preco_grupo_servico
	from	pls_proc_crit_hor_excecao
	where	nr_seq_regra_horario = nr_seq_regra_hor_p
	and	((coalesce(cd_procedimento::text, '') = '') or (cd_procedimento = cd_procedimento_p AND ie_origem_proced = ie_origem_proced_p));

BEGIN

open C01;
loop
fetch C01 into
	nr_seq_grupo_serv_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	ie_grupo_serv_w := 'S';
	if (coalesce(nr_seq_grupo_serv_w,0) > 0) then
		ie_grupo_serv_w	:= pls_se_grupo_preco_servico(nr_seq_grupo_serv_w, cd_procedimento_p, ie_origem_proced_p);
	end if;
	if (ie_grupo_serv_w = 'S') then
		ie_excecao_w := 'S';
	end if;
	end;
end loop;
close C01;

return	ie_excecao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
