CREATE OR REPLACE FUNCTION pls_obter_se_exclui_seg_peona ( nr_seq_segurado_p bigint, dt_referencia_p timestamp, 	-- Parâmetro adicionado para consistir a regra de acordo com a data das despesas ao invés da data atual - OS 481286 - ebcabral - 03/10/2012
 cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE


ie_tipo_vinculo_operadora_w	varchar(2);
ie_exclui_w			varchar(1)	:= 'N';
qt_regra_w			bigint;
nr_seq_regra_w			bigint	:= null;

C01 CURSOR FOR
	SELECT	a.nr_sequencia
	from	pls_regra_peona_exclusao	a
	where	((a.ie_tipo_vinculo_operadora = ie_tipo_vinculo_operadora_w) or (coalesce(a.ie_tipo_vinculo_operadora::text, '') = ''))
	and	dt_referencia_p between dt_inicio_vigencia and coalesce(dt_fim_vigencia,dt_referencia_p)
	and	cd_estabelecimento	= cd_estabelecimento_p
	order by
		coalesce(a.ie_tipo_vinculo_operadora,' ');


BEGIN
select	count(1)
into STRICT	qt_regra_w
from	pls_regra_peona_exclusao
where	cd_estabelecimento	= cd_estabelecimento_p  LIMIT 1;

if (qt_regra_w > 0) then
	select	max(a.ie_tipo_vinculo_operadora)
	into STRICT	ie_tipo_vinculo_operadora_w
	from	pls_segurado	a
	where	a.nr_sequencia	= nr_seq_segurado_p;

	open C01;
	loop
	fetch C01 into
		nr_seq_regra_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
	end loop;
	close C01;

	if (nr_seq_regra_w IS NOT NULL AND nr_seq_regra_w::text <> '') then
		ie_exclui_w	:= 'S';
	end if;
end if;

return	ie_exclui_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
