CREATE OR REPLACE FUNCTION pls_obter_se_exec_total_itens ( nr_seq_prestador_p bigint, ie_tipo_guia_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(2);

C01 CURSOR FOR
	SELECT	ie_executar_todos
	from (
		SELECT	ie_executar_todos,
			dt_inicio_vigencia
		from	pls_regra_execucao_total
		where	ie_aplicacao_regra	= 2
		and	ie_tipo_guia		= ie_tipo_guia_p
		and	clock_timestamp()	between dt_inicio_vigencia and fim_dia(coalesce(dt_fim_vigencia,clock_timestamp()))
		and (cd_estabelecimento = wheb_usuario_pck.get_cd_estabelecimento and (pls_obter_se_controle_estab('RE') = 'S'))
		
union all

		select	ie_executar_todos,
			dt_inicio_vigencia
		from	pls_regra_execucao_total
		where	ie_aplicacao_regra	= 2
		and	ie_tipo_guia		= ie_tipo_guia_p
		and	clock_timestamp()	between dt_inicio_vigencia and fim_dia(coalesce(dt_fim_vigencia,clock_timestamp()))
		and (pls_obter_se_controle_estab('RE') = 'N')) alias13
	order by dt_inicio_vigencia;


BEGIN

open C01;
loop
fetch C01 into
	ds_retorno_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
end loop;
close C01;

return	ds_retorno_w;

end;
$body$
