CREATE OR REPLACE FUNCTION pls_obter_se_exibe_portal_rec ( nr_seq_conta_p pls_conta.nr_sequencia%type, nr_seq_item_p pls_conta_proc.nr_sequencia%type, ie_tipo_item_p text) RETURNS varchar AS $body$
DECLARE


ie_exibe_portal_w	pls_lote_pagamento.ie_exibe_portal%type := 'S';
nr_seq_lote_pgto_w	pls_lote_pagamento.nr_sequencia%type;


BEGIN

if (ie_tipo_item_p = 'P') then

	select	max(nr_seq_lote_pgto)
	into STRICT	nr_seq_lote_pgto_w
	from	pls_conta_medica_resumo
	where	nr_seq_conta = nr_seq_conta_p
	and	nr_seq_conta_proc = nr_seq_item_p;
else
	select	max(nr_seq_lote_pgto)
	into STRICT	nr_seq_lote_pgto_w
	from	pls_conta_medica_resumo
	where	nr_seq_conta = nr_seq_conta_p
	and	nr_seq_conta_mat = nr_seq_item_p;
end if;

if (nr_seq_lote_pgto_w IS NOT NULL AND nr_seq_lote_pgto_w::text <> '') then
	select	coalesce(max(ie_exibe_portal),'S')
	into STRICT	ie_exibe_portal_w
	from	pls_lote_pagamento
	where	nr_sequencia = nr_seq_lote_pgto_w;
end if;

return	ie_exibe_portal_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
