CREATE OR REPLACE FUNCTION pls_obter_se_exige_espec ( nr_seq_conta_p bigint) RETURNS varchar AS $body$
DECLARE

 
 
cd_medico_w			varchar(10);
nr_seq_conselho_exec_w		bigint;
nr_seq_conselho_solic_w		bigint;
uf_crm_exec_w			varchar(2);
uf_crm_solic_imp_w		varchar(20);
qt_reg_w			bigint;
nr_seq_prestador_exec_w		bigint;
qt_esp_w			integer;
ie_cooperador_w			varchar(1);
ds_retorno_w			varchar(1) := 'N';
dt_conta_w			timestamp;


BEGIN 
 
select	cd_medico_executor, 
	nr_seq_conselho_exec, 
	nr_seq_conselho_solic, 
	uf_crm_exec, 
	uf_crm_solic_imp, 
	nr_seq_prestador_exec, 
	dt_atendimento_referencia 
into STRICT	cd_medico_w, 
	nr_seq_conselho_exec_w, 
	nr_seq_conselho_solic_w, 
	uf_crm_exec_w, 
	uf_crm_solic_imp_w, 
	nr_seq_prestador_exec_w, 
	dt_conta_w 
from	pls_conta 
where	nr_sequencia = nr_seq_conta_p;
 
 
if (cd_medico_w IS NOT NULL AND cd_medico_w::text <> '') then 
	select	CASE WHEN pls_obter_se_cooperado_ativo(cd_medico_w,dt_conta_w,null)='S' THEN 'C'  ELSE 'N' END  
	into STRICT	ie_cooperador_w 
	;
	 
	select	count(*) 
	into STRICT	qt_reg_w 
	from	pls_regra_exige_espec 
	where	nr_seq_conselho = nr_seq_conselho_exec_w 
	and	uf_crm = uf_crm_exec_w 
	and	ie_situacao = 'A' 
	and (ie_tipo_medico = ie_cooperador_w or ie_tipo_medico = 'A');
	 
	if (qt_reg_w > 0) then 
		 
		select	count(*) 
		into STRICT	qt_esp_w 
		from	medico_especialidade 
		where	cd_pessoa_fisica	= cd_medico_w;		
		 
		if (qt_esp_w = 0) then 
			ds_retorno_w := 'S';
		end if;
	end if;
end if;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
