SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_se_exige_senha_ext ( nr_seq_guia_p bigint, nr_seq_requisicao_p bigint) RETURNS varchar AS $body$
DECLARE


ie_exige_senha_ext_w		varchar(1);
cd_senha_externa_w		varchar(50);
ie_tipo_processo_w		pls_guia_plano.ie_tipo_processo%type;
ie_tipo_intercambio_w		pls_guia_plano.ie_tipo_intercambio%type;
nr_seq_intercambio_w		pls_segurado.nr_seq_intercambio%type;
ie_tipo_contrato_w		pls_intercambio.ie_tipo_contrato%type	:= 'X';
ie_retorno_w			varchar(1);


BEGIN

if (nr_seq_requisicao_p IS NOT NULL AND nr_seq_requisicao_p::text <> '') then
	select	coalesce(a.ie_exige_senha_ext,'N'),
		coalesce(a.cd_senha_externa,'X'),
		coalesce(a.ie_tipo_intercambio, 'X'),
		coalesce(a.ie_tipo_processo, 'X'),
		b.nr_seq_intercambio
	into STRICT	ie_exige_senha_ext_w,
		cd_senha_externa_w,
		ie_tipo_intercambio_w,
		ie_tipo_processo_w,
		nr_seq_intercambio_w
	from	pls_requisicao a,
		pls_segurado b
	where	a.nr_sequencia		= nr_seq_requisicao_p
	and	a.nr_seq_segurado	= b.nr_sequencia;

	if (nr_seq_intercambio_w IS NOT NULL AND nr_seq_intercambio_w::text <> '') then
		begin
			select	ie_tipo_contrato
			into STRICT	ie_tipo_contrato_w
			from	pls_intercambio
			where	nr_sequencia	= nr_seq_intercambio_w;
		exception
		when others then
			ie_tipo_contrato_w	:= 'X';
		end;


	end if;

	--Se for guia de intercâmbio externo
	if (ie_tipo_processo_w	= 'I') and (ie_tipo_intercambio_w	= 'E' ) and (ie_tipo_contrato_w = 'F') then
		ie_exige_senha_ext_w	:= 'S';
	end if;

elsif (nr_seq_guia_p IS NOT NULL AND nr_seq_guia_p::text <> '') then
	select	coalesce(a.ie_tipo_intercambio, 'X'),
		coalesce(a.ie_tipo_processo, 'X'),
		coalesce(a.cd_senha_externa,'X'),
		b.nr_seq_intercambio
	into STRICT	ie_tipo_intercambio_w,
		ie_tipo_processo_w,
		cd_senha_externa_w,
		nr_seq_intercambio_w
	from	pls_guia_plano a,
		pls_segurado b
	where	a.nr_sequencia		= nr_seq_guia_p
	and	a.nr_seq_segurado	= b.nr_sequencia;

	if (nr_seq_intercambio_w IS NOT NULL AND nr_seq_intercambio_w::text <> '') then
		begin
			select	ie_tipo_contrato
			into STRICT	ie_tipo_contrato_w
			from	pls_intercambio
			where	nr_sequencia	= nr_seq_intercambio_w;
		exception
		when others then
			ie_tipo_contrato_w	:= 'X';
		end;


	end if;

	--Se for guia de intercâmbio externo
	if (ie_tipo_processo_w	= 'I') and (ie_tipo_intercambio_w	= 'E' ) and (ie_tipo_contrato_w = 'F') then
		ie_exige_senha_ext_w	:= 'S';
	end if;

end if;

if (ie_exige_senha_ext_w	= 'S') and (cd_senha_externa_w	<> 'X') then
	ie_retorno_w	:= 'N';
elsif (ie_exige_senha_ext_w	= 'S') and (cd_senha_externa_w	= 'X') then
	ie_retorno_w	:= 'S';
elsif (ie_exige_senha_ext_w	= 'N') then
	ie_retorno_w	:= 'N';
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
