CREATE OR REPLACE FUNCTION pls_obter_se_existe_guia_ref ( nr_seq_guia_p text ) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1);


BEGIN

/*
  * Function que retorna se existe guia que utilizam a guia parametro como guia referencia
  * Diego OPS - 03/01/2011
  */
select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
into STRICT	ds_retorno_w
from	pls_guia_plano
where	nr_seq_guia_principal = nr_seq_guia_p;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
