CREATE OR REPLACE FUNCTION pls_obter_se_existe_mens_seg ( nr_seq_segurado_p bigint) RETURNS varchar AS $body$
DECLARE


qt_mensalidade_w	integer;


BEGIN

select	count(1)
into STRICT	qt_mensalidade_w
from	pls_mensalidade_seg_item c,
	pls_mensalidade_segurado b,
	pls_mensalidade a
where	a.nr_sequencia		= b.nr_seq_mensalidade
and	b.nr_sequencia		= c.nr_seq_mensalidade_seg
and	b.nr_seq_segurado	= nr_seq_segurado_p
and	coalesce(a.ie_cancelamento::text, '') = '';

if (qt_mensalidade_w > 0) then
	return	'S';
else
	return	'N';
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
