CREATE OR REPLACE FUNCTION pls_obter_se_fecha_conta ( nr_seq_motivo_glosa_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(1)	:= 'S';
cd_estabelecimento_w		estabelecimento.cd_estabelecimento%type;
ie_ocorrencia_w			pls_controle_estab.ie_ocorrencia%type := 'N';


BEGIN
cd_estabelecimento_w := wheb_usuario_pck.get_cd_estabelecimento;
ie_ocorrencia_w := pls_obter_se_controle_estab('GO');

begin
if (ie_ocorrencia_w = 'S') and (cd_estabelecimento_w IS NOT NULL AND cd_estabelecimento_w::text <> '') then
	select	coalesce(e.ie_fechar_conta,'S')
	into STRICT	ds_retorno_w
	from	pls_prestador_glosa	e,
		pls_outorgante_glosa	d,
		pls_glosa_evento	c,
		tiss_motivo_glosa	b,
		pls_conta_glosa		a
	where	a.nr_sequencia		= nr_seq_motivo_glosa_p
	and	a.nr_seq_motivo_glosa	= b.nr_sequencia
	and	b.nr_sequencia		= c.nr_seq_motivo_glosa
	and	c.nr_sequencia		= d.nr_seq_evento_glosa
	and	d.nr_sequencia		= e.nr_seq_outorg_glosa
	and	c.cd_estabelecimento	= cd_estabelecimento_w;
else
	select	coalesce(e.ie_fechar_conta,'S')
	into STRICT	ds_retorno_w
	from	pls_prestador_glosa	e,
		pls_outorgante_glosa	d,
		pls_glosa_evento	c,
		tiss_motivo_glosa	b,
		pls_conta_glosa		a
	where	a.nr_sequencia		= nr_seq_motivo_glosa_p
	and	a.nr_seq_motivo_glosa	= b.nr_sequencia
	and	b.nr_sequencia		= c.nr_seq_motivo_glosa
	and	c.nr_sequencia		= d.nr_seq_evento_glosa
	and	d.nr_sequencia		= e.nr_seq_outorg_glosa;
end if;
exception
	when others then
	begin
	if (ie_ocorrencia_w = 'S') and (cd_estabelecimento_w IS NOT NULL AND cd_estabelecimento_w::text <> '') then
		select	coalesce(d.ie_fechar_conta,'S')
		into STRICT	ds_retorno_w
		from	pls_outorgante_glosa	d,
			pls_glosa_evento	c,
			tiss_motivo_glosa	b,
			pls_conta_glosa		a
		where	a.nr_sequencia		= nr_seq_motivo_glosa_p
		and	a.nr_seq_motivo_glosa	= b.nr_sequencia
		and	b.nr_sequencia		= c.nr_seq_motivo_glosa
		and	c.nr_sequencia		= d.nr_seq_evento_glosa
		and	c.cd_estabelecimento	= cd_estabelecimento_w;
	else
		select	coalesce(d.ie_fechar_conta,'S')
		into STRICT	ds_retorno_w
		from	pls_outorgante_glosa	d,
			pls_glosa_evento	c,
			tiss_motivo_glosa	b,
			pls_conta_glosa		a
		where	a.nr_sequencia		= nr_seq_motivo_glosa_p
		and	a.nr_seq_motivo_glosa	= b.nr_sequencia
		and	b.nr_sequencia		= c.nr_seq_motivo_glosa
		and	c.nr_sequencia		= d.nr_seq_evento_glosa;
	end if;
	exception
		when others then
		select	coalesce(max(b.ie_fechar_conta),'S')
		into STRICT	ds_retorno_w
		from	tiss_motivo_glosa	b,
			pls_conta_glosa		a
		where	a.nr_sequencia		= nr_seq_motivo_glosa_p
		and	a.nr_seq_motivo_glosa	= b.nr_sequencia;
	end;
end;

return	ds_retorno_w;

end;
$body$
