CREATE OR REPLACE FUNCTION pls_obter_se_fim_carencias ( nr_sequencia_lista_p text, nr_seq_segurado_p bigint) RETURNS varchar AS $body$
DECLARE


nr_sequencia_lista_w	varchar(2000);
nr_sequencia_w		bigint;
dt_fim_carencia_w	timestamp;
ie_dt_fim_carencia_w	varchar(1);
ds_retorno_w		varchar(4000);


BEGIN
ds_retorno_w		:= '';
nr_sequencia_lista_w	:= nr_sequencia_lista_p;

while (nr_sequencia_lista_w IS NOT NULL AND nr_sequencia_lista_w::text <> '') loop
	begin
	nr_sequencia_w		:= substr(nr_sequencia_lista_w, 1, position(',' in nr_sequencia_lista_w) - 1);
	nr_sequencia_lista_w	:= substr(nr_sequencia_lista_w, position(',' in nr_sequencia_lista_w) + 1, length(nr_sequencia_lista_w));

	select	to_date(pls_obter_dados_carencia(nr_sequencia_w, nr_seq_segurado_p, 'DT'), 'dd/mm/yyyy')
	into STRICT	dt_fim_carencia_w
	;

	if (dt_fim_carencia_w >= clock_timestamp()) then
		ie_dt_fim_carencia_w	:= 'S';
	else
		ie_dt_fim_carencia_w	:= 'N';
	end if;

	ds_retorno_w	:= ds_retorno_w || ',' || nr_sequencia_w || ',' || ie_dt_fim_carencia_w;
	end;
end loop;

ds_retorno_w	:= substr(ds_retorno_w, 2, length(ds_retorno_w));

return	ds_retorno_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
