SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_se_fim_fluxo_analise (nr_seq_analise_p bigint, nr_seq_grupo_p bigint) RETURNS varchar AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Obter o último
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [ ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
ie_retorno_w		varchar(1)	:= 'N';
qt_registro_w		bigint;
nr_seq_ordem_w		bigint;


BEGIN

if (nr_seq_analise_p IS NOT NULL AND nr_seq_analise_p::text <> '') and (nr_seq_grupo_p IS NOT NULL AND nr_seq_grupo_p::text <> '') then
	select	max(a.nr_seq_ordem)
	into STRICT	nr_seq_ordem_w
	from	pls_auditoria_conta_grupo a
	where	a.nr_seq_analise	= nr_seq_analise_p;

	select	count(1)
	into STRICT	qt_registro_w
	from	pls_auditoria_conta_grupo a
	where	a.nr_seq_analise	= nr_seq_analise_p
	and	a.nr_seq_grupo		= nr_seq_grupo_p
	and	a.nr_seq_ordem		= nr_seq_ordem_w
	/* Não pode ter grupos com mesmo fluxo ainda não concluídos */

	and	not exists (SELECT	1
				from	pls_auditoria_conta_grupo x
				where	x.nr_seq_analise	= a.nr_seq_analise
				and	x.nr_seq_ordem		= a.nr_seq_ordem
				and	coalesce(x.dt_liberacao::text, '') = ''
				and	x.nr_sequencia		<> a.nr_sequencia);

	if (qt_registro_w > 0) then
		ie_retorno_w	:= 'S';
	end if;
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
