CREATE OR REPLACE FUNCTION pls_obter_se_gera_analise ( nr_seq_lote_p pls_lote_apres_automatica.nr_sequencia%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type, nm_usuario_p usuario.nm_usuario%type) RETURNS varchar AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: 	Verificar se o usuário possui a permissão para gerar análise,
	tratamento atualmente realizado direto no Delphi.
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  X]  Objetos do dicionário [ ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
ie_gera_w			varchar(1);
ie_permite_analise_w		varchar(1);
ie_permite_guia_nao_apr_w	varchar(1);

C01 CURSOR(nr_seq_lote_pc	pls_lote_apres_automatica.nr_sequencia%type)FOR
	SELECT	ie_origem_protocolo,
		ie_status,
		ie_guia_fisica,
		nr_sequencia nr_seq_protocolo
	from	pls_protocolo_conta
	where	nr_seq_lote_apres_autom = nr_seq_lote_pc;

BEGIN
for r_C01_w in C01(nr_seq_lote_p) loop
	ie_permite_analise_w := pls_obter_se_pode_acao_analise(Obter_Perfil_Ativo, null, null, 'S', r_C01_w.ie_origem_protocolo);
	-- Verifica se o perfil ativo tem permissão para gerar análise
	if (ie_permite_analise_w = 'S') then
		-- Caso a guia fisica não tenha sido apresentada e o status esteja diferente de encerrado sem pagamento
		if (r_C01_w.ie_guia_fisica = 'N') then
			-- verfica o parâmetro "[15] - Permitir enviar protocolo/lote para análise caso a guia física não tenha sido apresentada" da função "OPS - Controle de Produção Médica"
			ie_permite_guia_nao_apr_w := obter_valor_param_usuario(1285, 15, Obter_Perfil_Ativo, nm_usuario_p, cd_estabelecimento_p);

			if (ie_permite_guia_nao_apr_w = 'S') then

				ie_gera_w := 'S';
			else
				-- Caso o parâmetro 15 esteja para "Não", verifica as regras de validação de guia fisica
				ie_gera_w := pls_val_guia_fisica(null, r_C01_w.nr_seq_protocolo);

			end if;
		else
			--Caso a guia já foi apresentada logo libera
			ie_gera_w := 'S';
		end if;
	else
		-- Caso o usuário não tenha permissão, retornar 'N'
		ie_gera_w := 'N';
	end if;
end loop;


return	ie_gera_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_se_gera_analise ( nr_seq_lote_p pls_lote_apres_automatica.nr_sequencia%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type, nm_usuario_p usuario.nm_usuario%type) FROM PUBLIC;

