CREATE OR REPLACE FUNCTION pls_obter_se_grau_auxiliar (nr_seq_grau_partic_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1);


BEGIN

begin
	select	coalesce(ie_auxiliar,'N')
	into STRICT	ds_retorno_w
	from	pls_grau_participacao
	where   ie_situacao = 'A'
	and     nr_sequencia = nr_seq_grau_partic_p;
exception
when others then
	ds_retorno_w := null;
end;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
