CREATE OR REPLACE FUNCTION pls_obter_se_grupo_estip_web ( nr_seq_contrato_int_p bigint, nr_seq_contrato_p bigint, nr_seq_auditoria_p bigint) RETURNS bigint AS $body$
DECLARE


/*++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:   Obter se o grupo atual da análise é o mesmo do estipulante
----------------------------------------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [  ] Tasy (Delphi/Java) [ x ] Portal [  ] Relatórios [ ] Outros:
 ----------------------------------------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++*/nm_usuario_exec_w	varchar(15);
qt_registros_w		bigint;
nr_seq_grupo_w		bigint := null;
nr_seq_grupo_atual_w	bigint;
ie_status_aud_w		varchar(3);



BEGIN

select  max(ie_status)
into STRICT	ie_status_aud_w
from	pls_auditoria
where	nr_sequencia = nr_seq_auditoria_p;

-- Verifica se auditoria esta em analise
if (coalesce(ie_status_aud_w,'X') in ('A','AJ','AF','AN','AP')) then

	-- Verifica se o estipulante tem permissão para auditar
	if (coalesce(nr_seq_contrato_int_p,0) > 0) then
		select  max(a.nr_sequencia)
		into STRICT	nr_seq_grupo_w
		from	pls_grupo_auditor a,
			pls_membro_grupo_aud b
		where	a.nr_sequencia = b.nr_seq_grupo
		and	coalesce(a.ie_permissao, 'X') = 'T'
		and	a.ie_tipo_auditoria = 3
		and	b.nr_seq_intercambio  = nr_seq_contrato_int_p;
	elsif (coalesce(nr_seq_contrato_p,0) > 0) then
		select  max(a.nr_sequencia)
		into STRICT	nr_seq_grupo_w
		from	pls_grupo_auditor a,
			pls_membro_grupo_aud b
		where	a.nr_sequencia = b.nr_seq_grupo
		and	coalesce(a.ie_permissao, 'X') = 'T'
		and	a.ie_tipo_auditoria = 3
		and	b.nr_seq_contrato  = nr_seq_contrato_p;
	end if;

	if (nr_seq_grupo_w IS NOT NULL AND nr_seq_grupo_w::text <> '') then

		nr_seq_grupo_atual_w := pls_obter_grupo_analise_atual(nr_seq_auditoria_p);

		if (coalesce(nr_seq_grupo_atual_w,0) = 0) then
			nr_seq_grupo_w := null; -- Análise encerrada
		else
			-- Verifica se esta em análise ou se o grupo atual da análise é o mesmo do estipulante
			select	count(1)
			into STRICT	qt_registros_w
			from	pls_auditoria_grupo a
			where	a.nr_sequencia = nr_seq_grupo_atual_w
			and	((a.nm_usuario_exec IS NOT NULL AND a.nm_usuario_exec::text <> '')
			or (a.nr_seq_grupo = nr_seq_grupo_w));

			if (qt_registros_w > 0) then
				nr_seq_grupo_w := null;
			end if;
		end if;
	end if;
end if;

return	nr_seq_grupo_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_se_grupo_estip_web ( nr_seq_contrato_int_p bigint, nr_seq_contrato_p bigint, nr_seq_auditoria_p bigint) FROM PUBLIC;

