SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_se_grupo_final ( nr_seq_analise_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(1) := 'N';
qt_grupo_final_w		bigint;


BEGIN

/*Obter se existe grupo de finalização.*/

select	count(*)
into STRICT	qt_grupo_final_w
from	pls_fim_analise_conta
where	cd_estabelecimento 	= cd_estabelecimento_p
and	ie_situacao 		= 'A';

if (qt_grupo_final_w > 0) then
	/*Se existir, somente o grupo de finalização tem acesso ao botão fechar conta.*/

	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ds_retorno_w
	from	pls_fim_analise_conta
	where	pls_obter_se_auditor_grupo(nr_seq_grupo, nm_usuario_p) = 'S';
else
	/*Se não existir então é liberado para qualquer grupo finalizar.*/

	ds_retorno_w := 'S';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
