CREATE OR REPLACE FUNCTION pls_obter_se_grupo_iniciado ( nr_seq_grupo_atual_p bigint, nr_seq_analise_p bigint ) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(1)	:= 'N';


BEGIN

select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
into STRICT	ds_retorno_w
from	pls_tempo_conta_grupo 	  a,
	pls_auditoria_conta_grupo b
where	a.nr_seq_auditoria	= b.nr_sequencia
and	b.nr_seq_grupo 		= nr_seq_grupo_atual_p
and	b.nr_seq_analise	= nr_seq_analise_p
and	(a.dt_inicio_analise IS NOT NULL AND a.dt_inicio_analise::text <> '')
and	coalesce(a.dt_final_analise::text, '') = '';

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
