SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_se_grupo_liberado (nm_usuario_p text, nr_seq_analise_p text, nr_seq_grupo_atual_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1) := 'N';

/*
Retorna	'S' = Análise do grupo encerrada
	'N' = Análise do grupo aberta
*/
BEGIN

/*Caso seja grupo master o grupo é considerado como grupo não liberado para que o usuário possa realizar modificações a qualquer momento*/

if (pls_obter_se_usuario_master(nm_usuario_p,null) = 'S') then
	ds_retorno_w := 'N';
else
	select	CASE WHEN count(*)=0 THEN 'S'  ELSE 'N' END
	into STRICT	ds_retorno_w
	from	pls_auditoria_conta_grupo
	where	nr_seq_grupo	= nr_seq_grupo_atual_p
	and	nr_seq_analise	= nr_seq_analise_p
	and	coalesce(dt_liberacao::text, '') = '';
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
