CREATE OR REPLACE FUNCTION pls_obter_se_grupo_pacote ( nr_seq_grupo_p bigint, nr_seq_conta_p bigint, nr_seq_guia_p bigint, cd_procedimento_p bigint, ie_origem_proced_p bigint) RETURNS varchar AS $body$
DECLARE


cd_procedimento_w		bigint;
ie_origem_proc_w		bigint;
qt_procedimento_w		bigint;
ds_retorno_w			varchar(10)	:= 'N';
qt_procedimento_ww		bigint;
qt_reg_w			integer;
qt_pacote_w			bigint;

C01 CURSOR FOR
	SELECT	a.cd_procedimento,
		a.ie_origem_proced
	from	pls_pacote_grupo_proc		a,
		pls_pacote_cad_grupo_proc	b
	where	a.nr_seq_grupo	= b.nr_sequencia
	and	b.ie_situacao	= 'A'
	and	a.ie_situacao	= 'A'
	and	b.nr_sequencia	= nr_seq_grupo_p;


BEGIN

if (nr_seq_conta_p IS NOT NULL AND nr_seq_conta_p::text <> '') then
	open C01;
	loop
	fetch C01 into
		cd_procedimento_w,
		ie_origem_proc_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		begin
		select	count(nr_sequencia)
		into STRICT	qt_procedimento_ww
		from	pls_conta_proc
		where	nr_seq_conta		= nr_seq_conta_p
		and	cd_procedimento		= cd_procedimento_w
		and	ie_origem_proced	= ie_origem_proc_w;
		exception
		when others then
			qt_procedimento_ww	:= 0;
		end;

		if (qt_procedimento_ww <> 0) then
			qt_procedimento_ww	:= 1;
		end if;

		qt_procedimento_w	:= coalesce(qt_procedimento_w,0) + qt_procedimento_ww;

		select	count(*)
		into STRICT	qt_pacote_w
		from	pls_pacote_grupo_proc		a,
			pls_pacote_cad_grupo_proc	b
		where	a.nr_seq_grupo	= b.nr_sequencia
		and	b.ie_situacao	= 'A'
		and	a.ie_situacao	= 'A'
		and	b.nr_sequencia	= nr_seq_grupo_p;
		if (qt_procedimento_w	<> 0) and (qt_pacote_w		<= qt_procedimento_w) then
			ds_retorno_w	:= 'S';
		end if;
		end;
	end loop;
	close C01;
elsif (nr_seq_guia_p IS NOT NULL AND nr_seq_guia_p::text <> '') then
	select	count(1)
	into STRICT	qt_reg_w
	from	pls_pacote_grupo_proc		a,
		pls_pacote_cad_grupo_proc	b
	where	a.nr_seq_grupo	= b.nr_sequencia
	and	b.nr_sequencia	= nr_seq_grupo_p
	and	b.ie_situacao	= 'A'
	and	a.ie_situacao	= 'A'
	and	cd_procedimento		= cd_procedimento_p
	and	ie_origem_proced	= ie_origem_proced_p;

	if (qt_reg_w > 0) then
		ds_retorno_w	:= 'S';
	end if;
end if;
return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
