CREATE OR REPLACE FUNCTION pls_obter_se_grupo_pend_rec ( nr_seq_analise_p pls_analise_conta.nr_sequencia%type, nr_seq_proc_rec_p pls_rec_glosa_proc.nr_sequencia%type, nr_seq_mat_rec_p pls_rec_glosa_mat.nr_sequencia%type, nr_seq_grupo_p pls_grupo_auditor.nr_sequencia%type, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*
ie_opcao_p
'A' - An√°lise
'I' - Item
*/
ds_retorno_w	varchar(1) := 'N';
qt_glosa_w	integer := 0;

C01 CURSOR(nr_seq_analise_pc		pls_analise_conta.nr_sequencia%type) FOR
	SELECT	distinct a.nr_seq_conta_rec,
		null nr_seq_proc_rec,
		null nr_seq_mat_rec
	from	pls_analise_conta_rec a
	where	a.nr_seq_analise = nr_seq_analise_pc
	and	a.ie_situacao = 'N'
	and	not exists (	SELECT	1
				from	pls_analise_parecer_rec b
				where	b.nr_seq_proc_rec = a.nr_seq_proc_rec)
	and	not exists (	select	1
				from	pls_analise_parecer_rec b
				where	b.nr_seq_mat_rec = a.nr_seq_mat_rec)
	
union all

	select	null nr_seq_conta_rec,
		a.nr_seq_proc_rec,
		null nr_seq_mat_rec
	from	pls_analise_conta_rec a
	where	a.nr_seq_analise = nr_seq_analise_pc
	and	(a.nr_seq_proc_rec IS NOT NULL AND a.nr_seq_proc_rec::text <> '')
	and	a.ie_situacao = 'N'
	and	not exists (	select	1
				from	pls_analise_parecer_rec b
				where	b.nr_seq_proc_rec = a.nr_seq_proc_rec)
	
union all

	select	null nr_seq_conta_rec,
		null nr_seq_proc_rec,
		a.nr_seq_mat_rec
	from	pls_analise_conta_rec a
	where	a.nr_seq_analise = nr_seq_analise_pc
	and	(a.nr_seq_mat_rec IS NOT NULL AND a.nr_seq_mat_rec::text <> '')
	and	a.ie_situacao = 'N'
	and	not exists (	select	1
				from	pls_analise_parecer_rec b
				where	b.nr_seq_mat_rec = a.nr_seq_mat_rec);

C02 CURSOR(	nr_seq_conta_rec_pc	pls_rec_glosa_conta.nr_sequencia%type,
		nr_seq_proc_rec_pc	pls_rec_glosa_proc.nr_sequencia%type,
		nr_seq_conta_mat_pc	pls_rec_glosa_mat.nr_sequencia%type) FOR
	SELECT	a.nr_seq_motivo_glosa
	from	pls_rec_glosa_glosas a
	where	a.nr_seq_conta_rec = nr_seq_conta_rec_pc
	and	coalesce(nr_seq_proc_rec_pc::text, '') = ''
	and	coalesce(nr_seq_conta_mat_pc::text, '') = ''
	
union all

	SELECT	a.nr_seq_motivo_glosa
	from	pls_rec_glosa_glosas a
	where	a.nr_seq_proc_rec = nr_seq_proc_rec_pc
	and	coalesce(nr_seq_conta_rec_pc::text, '') = ''
	and	coalesce(nr_seq_conta_mat_pc::text, '') = ''
	
union all

	select	a.nr_seq_motivo_glosa
	from	pls_rec_glosa_glosas a
	where	a.nr_seq_mat_rec = nr_seq_conta_mat_pc
	and	coalesce(nr_seq_conta_rec_pc::text, '') = ''
	and	coalesce(nr_seq_proc_rec_pc::text, '') = '';

C03 CURSOR(nr_seq_motivo_glosa_pc		tiss_motivo_glosa.nr_sequencia%type) FOR
	SELECT	a.nr_sequencia
	from	pls_glosa_flux_analise_rec a
	where	a.nr_seq_motivo_glosa = nr_seq_motivo_glosa_pc
	
union all

	SELECT	a.nr_sequencia
	from	pls_glosa_flux_analise_rec a
	where	coalesce(a.nr_seq_motivo_glosa::text, '') = '';
BEGIN
if (ie_opcao_p = 'A') then
	if (nr_seq_analise_p IS NOT NULL AND nr_seq_analise_p::text <> '') then

		for r_C01_w in C01(nr_seq_analise_p) loop

			for r_C02_w in C02(r_C01_w.nr_seq_conta_rec, r_C01_w.nr_seq_proc_rec, r_C01_w.nr_seq_mat_rec) loop

				for r_C03_w in C03(r_C02_w.nr_seq_motivo_glosa) loop

					select	count(1)
					into STRICT	qt_glosa_w
					from	pls_fluxo_analise_rec
					where	nr_seq_glosa_flux_rec = r_C03_w.nr_sequencia
					and	nr_seq_grupo = nr_seq_grupo_p;

					if (qt_glosa_w > 0) then
						goto final;
					end if;
				end loop;
			end loop;
		end loop;
	end if;
elsif (ie_opcao_p = 'I') then

	for r_C02_w in C02(null, nr_seq_proc_rec_p, nr_seq_mat_rec_p) loop

		for r_C03_w in C03(r_C02_w.nr_seq_motivo_glosa) loop

			select	count(1)
			into STRICT	qt_glosa_w
			from	pls_fluxo_analise_rec
			where	nr_seq_glosa_flux_rec = r_C03_w.nr_sequencia
			and	nr_seq_grupo = nr_seq_grupo_p;

			if (qt_glosa_w > 0) then
				goto final;
			end if;
		end loop;
	end loop;
end if;

<<final>>

if (qt_glosa_w > 0) then
	ds_retorno_w := 'S';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
