CREATE OR REPLACE FUNCTION pls_obter_se_grupo_tipo_desp (nr_seq_grupo_p bigint, ie_tipo_despesa_p text, ie_tipo_p text) RETURNS varchar AS $body$
DECLARE


ie_retorno_w		varchar(1)	:= null;
ie_tipo_despesa_w	varchar(1);
nr_seq_regra_w		bigint;
qt_oco_despesa_w	bigint;

c01 CURSOR FOR
	SELECT	a.nr_sequencia,
		CASE WHEN ie_tipo_p='P' THEN a.ie_tipo_despesa_proc  ELSE a.ie_tipo_despesa_mat END
	from	pls_oc_grupo_tipo_desp	a
	where	a.nr_seq_ocorrencia_grupo	= nr_seq_grupo_p
	--and	((nvl(a.ie_tipo_despesa_proc,nvl(ie_tipo_despesa_p,' ')) = nvl(ie_tipo_despesa_p,' ') and ie_tipo_p = 'P') or
	--	(nvl(a.ie_tipo_despesa_mat,nvl(ie_tipo_despesa_p,' ')) = nvl(ie_tipo_despesa_p,' ') and ie_tipo_p = 'M'))
	and ((a.ie_tipo_despesa_proc IS NOT NULL AND a.ie_tipo_despesa_proc::text <> '') or ie_tipo_p = 'M')
	and ((a.ie_tipo_despesa_mat IS NOT NULL AND a.ie_tipo_despesa_mat::text <> '') or ie_tipo_p = 'P')
	order by
		coalesce(a.ie_tipo_despesa_mat,' '),
		coalesce(a.ie_tipo_despesa_proc,' ');


BEGIN

select	count(1)
into STRICT	qt_oco_despesa_w
from	pls_oc_grupo_tipo_desp
where	nr_seq_ocorrencia_grupo = nr_seq_grupo_p;

if (qt_oco_despesa_w = 0) then
	ie_retorno_w := 'S';
	goto final;
end if;

ie_retorno_w	:= 'N';
open C01;
loop
fetch C01 into
	nr_seq_regra_w,
	ie_tipo_despesa_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	if (ie_tipo_despesa_p = ie_tipo_despesa_w) then
		ie_retorno_w	:= 'S';
		goto final;
	end if;
	end;
end loop;
close C01;

<<final>>
ie_retorno_w	:= coalesce(ie_retorno_w,'S');

return ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
