CREATE OR REPLACE FUNCTION pls_obter_se_grupo_uf ( nr_seq_grupo_p bigint, uf_p text) RETURNS varchar AS $body$
DECLARE


ie_retorno_w		varchar(1)	:= 'N';
qt_regra_w		integer;


BEGIN

select	COUNT(1)
into STRICT	qt_regra_w
from	pls_preco_uf		a,
	pls_preco_grupo_uf	b
where	a.nr_seq_grupo		= b.nr_sequencia
and	a.sg_uf			= uf_p
and	b.nr_sequencia		= nr_seq_grupo_p
and	b.ie_situacao		= 'A';

if (qt_regra_w > 0) then
	ie_retorno_w	:= 'S';
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
