SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_se_guia_aprest ( nr_seq_guia_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1) := 'N';
cd_guia_w		varchar(20);
qt_conta_w		bigint := 0;
qt_conta_prot_pend_w	bigint := 0;


BEGIN

select  count(1)
into STRICT	qt_conta_w
from 	pls_conta  a,
	pls_guia_plano b
where 	a.cd_guia = b.cd_guia
and	b.nr_sequencia = nr_seq_guia_p
and	a.ie_status <> 'U'
and	a.ie_status <> 'P'
and	a.ie_status <> 'C'
and	a.nr_seq_segurado =  b.nr_seq_segurado;

if (qt_conta_w <> 0) then

	select	max(cd_guia)
	into STRICT	cd_guia_w
	from	pls_guia_plano
	where 	nr_sequencia = nr_seq_guia_p;

	if (cd_guia_w IS NOT NULL AND cd_guia_w::text <> '') then
		-- caso o protocolo da mesma n√£o esteja finalizado
		select  count(1)
		into STRICT	qt_conta_prot_pend_w
		from	pls_conta a,
			pls_protocolo_conta b
		where   a.nr_seq_protocolo = b.nr_sequencia
		and	a.cd_guia = cd_guia_w
		and	b.ie_situacao = 'I';

		if (qt_conta_w <> 0 and (qt_conta_w <> qt_conta_prot_pend_w)) then
			ds_retorno_w := 'S';
		end if;
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
