CREATE OR REPLACE FUNCTION pls_obter_se_guia_auditoria ( nr_seq_guia_p bigint) RETURNS bigint AS $body$
DECLARE


nr_retorno_w			integer;
qt_guia_proc_auditoria_w	integer := 0;
qt_guia_mat_auditoria_w		integer := 0;
qt_guia_plano_ocorrencia_w	integer := 0;
ie_estagio_w			pls_guia_plano.ie_estagio%type;
ie_origem_solic_w		pls_guia_plano.ie_origem_solic%type;


BEGIN

select	count(1)
into STRICT	qt_guia_proc_auditoria_w
from	pls_guia_plano_proc
where	nr_seq_guia	= nr_seq_guia_p
and	ie_status	= 'A'
and	coalesce(nr_seq_motivo_exc::text, '') = '';

select	count(1)
into STRICT	qt_guia_mat_auditoria_w
from	pls_guia_plano_mat
where	nr_seq_guia	= nr_seq_guia_p
and	ie_status	= 'A'
and	coalesce(nr_seq_motivo_exc::text, '') = '';

begin
select	ie_estagio,
	ie_origem_solic
into STRICT	ie_estagio_w,
	ie_origem_solic_w
from	pls_guia_plano
where	nr_sequencia	= nr_seq_guia_p;
exception
when others then
	ie_estagio_w := 0;
end;

if (ie_estagio_w = 7) or (ie_origem_solic_w = 'E') then
	select	count(1)
	into STRICT	qt_guia_plano_ocorrencia_w
	from	pls_ocorrencia		b,
		pls_ocorrencia_benef	a
	where	a.nr_seq_guia_plano = nr_seq_guia_p
	and	a.nr_seq_ocorrencia = b.nr_sequencia
	and	b.ie_auditoria = 'S'
	and	coalesce(a.nr_seq_proc::text, '') = ''
	and	coalesce(a.nr_seq_mat::text, '') = '';
end if;

nr_retorno_w := qt_guia_proc_auditoria_w + qt_guia_mat_auditoria_w + qt_guia_plano_ocorrencia_w;

return	nr_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_se_guia_auditoria ( nr_seq_guia_p bigint) FROM PUBLIC;

