CREATE OR REPLACE FUNCTION pls_obter_se_guia_auditor ( nr_seq_guia_p bigint, ie_tipo_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


/* IE_TIPO_P
	1 - Autorização
	2 - Contas médicas
*/
qt_registro_w			integer	:= 0;
qt_procedimentos_w		integer	:= 0;
ie_retorno_w			varchar(1)	:= 'S';
nr_seq_regra_liberacao_w	bigint;
ie_estagio_w			smallint;
cd_pessoa_fisica_w		varchar(10);


BEGIN

begin
select	coalesce(nr_seq_regra_liberacao,0),
	ie_estagio
into STRICT	nr_seq_regra_liberacao_w,
	ie_estagio_w
from	pls_guia_plano
where	nr_sequencia	= nr_seq_guia_p;
exception
	when others then
	nr_seq_regra_liberacao_w	:= 0;
	ie_estagio_w			:= 0;
end;

if (ie_estagio_w = 1) and (nr_seq_regra_liberacao_w > 0) then
	/* Obter a pessoa física do usuário */

	/*select 	nvl(max(cd_pessoa_fisica),'')
	into	cd_pessoa_fisica_w
	from	usuario
	where	nm_usuario	= nm_usuario_p;*/
	select	count(*)
	into STRICT	qt_registro_w
	from	pls_membro_grupo_aud	d,
		pls_grupo_auditor	c,
		pls_lib_grupo_auditor	b,
		pls_regra_liberacao	a
	where	a.nr_sequencia		= nr_seq_regra_liberacao_w
	and	d.nm_usuario_exec	= nm_usuario_p
	and	a.nr_sequencia		= b.nr_seq_regra_lib
	and	b.nr_seq_grupo_auditor	= c.nr_sequencia
	and	c.nr_sequencia		= d.nr_seq_grupo
	and	c.ie_situacao		= 'A'
	and	d.ie_situacao		= 'A';

	if (qt_registro_w	= 0) then
		ie_retorno_w	:= 'N';
	end if;
end if;

select	count(*)
into STRICT	qt_procedimentos_w
from	pls_guia_plano_proc
where	nr_seq_guia	= nr_seq_guia_p
and	ie_status	= 'A'
and	nr_seq_regra_liberacao > 0;

if (qt_procedimentos_w	> 0) then
	select	count(*)
	into STRICT	qt_registro_w
	from	pls_guia_plano_proc
	where	nr_seq_guia	= nr_seq_guia_p
	and	ie_status	= 'A'
	and	nr_seq_regra_liberacao > 0
	and	pls_obter_se_proc_auditor(nr_sequencia, 1, nm_usuario_p) = 'S';

	if (qt_registro_w	= 0) then
		ie_retorno_w	:= 'N';
	else
		ie_retorno_w	:= 'S';
	end if;
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
