CREATE OR REPLACE FUNCTION pls_obter_se_guia_aud_ocor ( nr_seq_guia_p bigint) RETURNS bigint AS $body$
DECLARE


nr_retorno_w			integer;
qt_guia_proc_auditoria_w	integer := 0;
qt_guia_mat_auditoria_w		integer := 0;
qt_guia_plano_ocorrencia_w	integer := 0;
ie_estagio_w			smallint;


BEGIN

select	count(*)
into STRICT	qt_guia_proc_auditoria_w
from	pls_ocorrencia_benef	b,
	pls_guia_plano_proc	a
where	a.nr_seq_guia	= nr_seq_guia_p
and	a.nr_sequencia	= b.nr_seq_proc
and	a.ie_status	= 'A';

select	count(*)
into STRICT	qt_guia_mat_auditoria_w
from	pls_ocorrencia_benef	b,
	pls_guia_plano_mat	a
where	a.nr_seq_guia	= nr_seq_guia_p
and	a.nr_sequencia	= b.nr_seq_mat
and	a.ie_status	= 'A';

begin
select	ie_estagio
into STRICT	ie_estagio_w
from	pls_guia_plano
where	nr_sequencia	= nr_seq_guia_p;
exception
when others then
	ie_estagio_w := 0;
end;

if (ie_estagio_w = 7) then
	select	count(*)
	into STRICT	qt_guia_plano_ocorrencia_w
	from	pls_ocorrencia_benef	b,
		pls_ocorrencia		a
	where	b.nr_seq_guia_plano = nr_seq_guia_p
	and	b.nr_seq_ocorrencia = a.nr_sequencia
	and	a.ie_auditoria = 'S'
	and	coalesce(nr_seq_proc::text, '') = ''
	and	coalesce(nr_seq_mat::text, '') = '';
end if;

nr_retorno_w := qt_guia_proc_auditoria_w + qt_guia_mat_auditoria_w + qt_guia_plano_ocorrencia_w;

return	nr_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
