CREATE OR REPLACE FUNCTION pls_obter_se_guia_glosada ( nr_seq_guia_p bigint) RETURNS varchar AS $body$
DECLARE


ie_retorno_w			varchar(1)	:= 'N';
qt_glosa_w			integer	:= 0;
qt_glosa_guia_w			integer	:= 0;
qt_glosa_guia_proc_w		integer	:= 0;
qt_glosa_guia_mat_w		integer	:= 0;


BEGIN

select	count(*)
into STRICT	qt_glosa_guia_w
from	pls_guia_glosa
where	nr_seq_guia = nr_seq_guia_p;

select	count(*)
into STRICT	qt_glosa_guia_proc_w
from	pls_guia_glosa
where	nr_seq_guia_proc in (	SELECT	nr_sequencia
				from 	pls_guia_plano_proc
				where 	nr_seq_guia = nr_seq_guia_p
				and	coalesce(nr_seq_motivo_exc::text, '') = '')
and (coalesce(nr_seq_ocorrencia::text, '') = ''
or	nr_seq_ocorrencia in (	select  a.nr_sequencia
				from	pls_ocorrencia a,
					pls_ocorrencia_benef b
				where	a.nr_sequencia = b.nr_seq_ocorrencia
				and	b.nr_seq_guia_plano = nr_seq_guia_p
				and	a.ie_auditoria <> 'S'));

select	count(*)
into STRICT	qt_glosa_guia_mat_w
from	pls_guia_glosa
where	nr_seq_guia_mat in (	SELECT	nr_sequencia
				from 	pls_guia_plano_mat
				where 	nr_seq_guia = nr_seq_guia_p
				and	coalesce(nr_seq_motivo_exc::text, '') = '')
and (coalesce(nr_seq_ocorrencia::text, '') = ''
or	nr_seq_ocorrencia in (	select  a.nr_sequencia
				from	pls_ocorrencia a,
					pls_ocorrencia_benef b
				where	a.nr_sequencia = b.nr_seq_ocorrencia
				and	b.nr_seq_guia_plano = nr_seq_guia_p
				and	a.ie_auditoria <> 'S'));

qt_glosa_w	:= qt_glosa_guia_w + qt_glosa_guia_proc_w + qt_glosa_guia_mat_w;

if (qt_glosa_w	> 0) then
	ie_retorno_w	:= 'S';
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_se_guia_glosada ( nr_seq_guia_p bigint) FROM PUBLIC;

