CREATE OR REPLACE FUNCTION pls_obter_se_guia_glosa ( nr_seq_guia_p bigint, nr_seq_guia_proc_p bigint, nr_seq_guia_mat_p bigint) RETURNS varchar AS $body$
DECLARE


nr_seq_guia_proc_w		bigint;
nr_seq_guia_mat_w		bigint;
nr_seq_glosa_w			bigint;
qt_registros_w			bigint := 0;
ds_retorno_w			varchar(1);

c010 CURSOR FOR
	SELECT	nr_sequencia
	from	pls_guia_plano_proc
	where	nr_seq_guia	= nr_seq_guia_p
	and 	coalesce(nr_seq_motivo_exc::text, '') = '';

c011 CURSOR FOR
	SELECT	nr_sequencia
	from	pls_guia_plano_mat
	where	nr_seq_guia	= nr_seq_guia_p
	and 	coalesce(nr_seq_motivo_exc::text, '') = '';


BEGIN

if (nr_seq_guia_p IS NOT NULL AND nr_seq_guia_p::text <> '') then
	select	count(*)
	into STRICT	qt_registros_w
	from	pls_guia_glosa
	where	nr_seq_guia	= nr_seq_guia_p;

	open c010;
	loop
	fetch c010 into	nr_seq_guia_proc_w;
	EXIT WHEN NOT FOUND; /* apply on c010 */
		select	coalesce(max(nr_sequencia),0)
		into STRICT	nr_seq_glosa_w
		from	pls_guia_glosa
		where	nr_seq_guia_proc	= nr_seq_guia_proc_w;
		if (nr_seq_glosa_w <> 0) then
			qt_registros_w	:= qt_registros_w + 1;
		end if;
	end loop;
	close c010;

	open c011;
	loop
	fetch c011 into	nr_seq_guia_mat_w;
	EXIT WHEN NOT FOUND; /* apply on c011 */
		select	coalesce(max(nr_sequencia),0)
		into STRICT	nr_seq_glosa_w
		from	pls_guia_glosa
		where	nr_seq_guia_mat	= nr_seq_guia_mat_w;
		if (nr_seq_glosa_w <> 0) then
			qt_registros_w	:= qt_registros_w + 1;
		end if;
	end loop;
	close c011;
elsif (nr_seq_guia_proc_p IS NOT NULL AND nr_seq_guia_proc_p::text <> '') then
	select	count(*)
	into STRICT	qt_registros_w
	from	pls_guia_glosa
	where	nr_seq_guia_proc	= nr_seq_guia_proc_p;
elsif (nr_seq_guia_mat_p IS NOT NULL AND nr_seq_guia_mat_p::text <> '') then
	select	count(*)
	into STRICT	qt_registros_w
	from	pls_guia_glosa
	where	nr_seq_guia_mat		= nr_seq_guia_mat_p;
end if;

if (qt_registros_w > 0) then
	ds_retorno_w	:= 'S';
else
	ds_retorno_w	:= 'N';
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
