CREATE OR REPLACE FUNCTION pls_obter_se_guia_intercambio ( cd_guia_p pls_guia_plano.cd_guia %type, nr_seq_guia_p pls_guia_plano.nr_sequencia%type) RETURNS varchar AS $body$
DECLARE


ie_tipo_processo_w	pls_guia_plano.ie_tipo_processo%type;

BEGIN

if (cd_guia_p IS NOT NULL AND cd_guia_p::text <> '')	then
	begin
		select	ie_tipo_processo
		into STRICT	ie_tipo_processo_w
		from	pls_guia_plano
		where 	cd_guia = cd_guia_p;
	exception
	when others then
		ie_tipo_processo_w := null;
	end;
elsif (nr_seq_guia_p IS NOT NULL AND nr_seq_guia_p::text <> '')	then
	begin
		select	ie_tipo_processo
		into STRICT	ie_tipo_processo_w
		from	pls_guia_plano
		where	nr_sequencia = nr_seq_guia_p;
	exception
	when others then
		ie_tipo_processo_w := null;
	end;
end if;

if (ie_tipo_processo_w = 'I') then
	return 'S';
else
	return 'N';
end if;

end;
