SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_se_horas_op_invalido ( nr_seq_operador_p bigint, ds_horas_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(255) := '';
dt_entrada_w			timestamp;
dt_saida_w			timestamp;
dt_fim_w			timestamp;


BEGIN


if ((substr(ds_horas_p,1,2))::numeric  > 23) or ((substr(ds_horas_p,3,4))::numeric  > 59) then
	ds_retorno_w := 'Horário invalido. Favor informar um horario menor que 23:59.';
else

	select	dt_entrada
	into STRICT	dt_entrada_w
	from	pls_operador_controle
	where	nr_sequencia = (SELECT	max(nr_sequencia)
				from	pls_operador_controle
				where	nr_seq_operador = nr_seq_operador_p);

	dt_saida_w := to_date(to_char(dt_entrada_w,'dd/mm/yyyy')||' '||ds_horas_p||':00','dd/mm/yyyy hh24:mi:ss');

	select	max(dt_fim_atendimento)
	into STRICT	dt_fim_w
	from	pls_atendimento
	where	nr_seq_operador = nr_seq_operador_p;

	if (dt_saida_w < dt_fim_w) then
		ds_retorno_w := 'Horário invalido. Favor informar um horario que seja posterior a data de finalização do ultimo atendimento.';
	elsif (dt_saida_w < dt_entrada_w) then
		ds_retorno_w := 'Horário invalido. Favor informar um horario que seja posterior a data de entrada do operador.';
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
