CREATE OR REPLACE FUNCTION pls_obter_se_insere_proc ( cd_procedimento_p bigint, ie_origem_proced_p bigint, ie_dbpanel_p bigint) RETURNS varchar AS $body$
DECLARE

/*
IE_DBPANEL_P:
1 - AMB
2 - CBHPM
3 - Servi√ßos
*/
ie_retorno_w			varchar(1):= 'S';
ie_classificacao_w		varchar(1);
cd_procedimento_w		bigint;
ie_origem_proced_w		integer;


BEGIN

if (ie_dbpanel_p in (1,2) )  then
	ie_classificacao_w	:= 1; /*procedimentos*/
	select 	max(cd_procedimento),
		max(ie_origem_proced)
	into STRICT	cd_procedimento_w,
		ie_origem_proced_w
	from 	procedimento
	where 	cd_procedimento  	= cd_procedimento_p
	and	ie_origem_proced 	= ie_origem_proced_p
	and	ie_classificacao 	= ie_classificacao_w;

elsif (ie_dbpanel_p = 3) then

	select 	max(cd_procedimento),
		max(ie_origem_proced)
	into STRICT	cd_procedimento_w,
		ie_origem_proced_w
	from 	procedimento
	where 	cd_procedimento  	= cd_procedimento_p
	and	ie_origem_proced 	= ie_origem_proced_p
	and	ie_classificacao in (2,3);
end if;

if (coalesce(cd_procedimento_w,0) = 0) or (coalesce(ie_origem_proced_w,0) = 0) then
	ie_retorno_w	:= 'N';
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
