CREATE OR REPLACE FUNCTION pls_obter_se_intercambio_limit ( nr_seq_intercambio_p bigint, nr_seq_plano_contrato_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(10);
qt_registros_w		bigint;


BEGIN

ds_retorno_w	:= 'N';

if (nr_seq_intercambio_p IS NOT NULL AND nr_seq_intercambio_p::text <> '') then
	select	count(1)
	into STRICT	qt_registros_w
	
	where	exists (SELECT	1
			from	pls_limitacao
			where	nr_seq_intercambio	= nr_seq_intercambio_p
			and	((nr_seq_plano_contrato = nr_seq_plano_contrato_p) or (coalesce(nr_seq_plano_contrato::text, '') = '')));

	if (qt_registros_w > 0) then
		ds_retorno_w	:= 'S';
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
