CREATE OR REPLACE FUNCTION pls_obter_se_internacao_guia ( nr_seq_guia_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(1) := 'N';
qt_reg_w			bigint;
nr_seq_guia_atend_w		bigint;


BEGIN

if (nr_seq_guia_p IS NOT NULL AND nr_seq_guia_p::text <> '') then

	select	count(1)
	into STRICT	qt_reg_w
	from	pls_guia_plano
	where	nr_seq_guia_principal = nr_seq_guia_p;

	if (qt_reg_w > 0) then

		select	max(a.nr_sequencia)
		into STRICT	nr_seq_guia_atend_w
		from	pls_guia_atendimento	b,
			pls_guia_plano		a
		where	b.nr_seq_guia	= a.nr_sequencia
		and	b.ie_tipo_registro = 1
		and	a.nr_seq_guia_principal = nr_seq_guia_p;

		if (nr_seq_guia_atend_w IS NOT NULL AND nr_seq_guia_atend_w::text <> '') then
			select	count(1)
			into STRICT	qt_reg_w
			from	pls_guia_atendimento
			where	nr_seq_guia = nr_seq_guia_atend_w
			and	ie_tipo_registro = 1
			and	(dt_alta IS NOT NULL AND dt_alta::text <> '');

			if (qt_reg_w = 0) then
				ds_retorno_w := 'S';
			end if;
		else
			select	count(1)
			into STRICT	qt_reg_w
			from	pls_guia_atendimento
			where	nr_seq_guia = nr_seq_guia_p
			and	ie_tipo_registro = 1
			and	(dt_alta IS NOT NULL AND dt_alta::text <> '');

			if (qt_reg_w = 0) then
				ds_retorno_w := 'S';
			end if;
		end if;
	else
		select	count(1)
		into STRICT	qt_reg_w
		from	pls_guia_atendimento
		where	nr_seq_guia = nr_seq_guia_p
		and	ie_tipo_registro = 1
		and	(dt_alta IS NOT NULL AND dt_alta::text <> '');

		if (qt_reg_w = 0) then
			ds_retorno_w := 'S';
		end if;
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
