CREATE OR REPLACE FUNCTION pls_obter_se_internado_imp ( nr_seq_conta_p pls_conta.nr_sequencia%type, ie_tipo_p text, ie_tipo_guia_p pls_conta.ie_tipo_guia%type default null, nr_seq_tipo_atend_p pls_conta.nr_seq_tipo_atendimento%type default null, ie_tipo_atend_imp_p pls_conta.ie_tipo_atendimento_imp%type default null, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type default null, ie_pela_seq_p text default 'S', ie_regime_atendimento_p pls_conta.ie_regime_atendimento%type default null, ie_saude_ocupacional_p pls_conta.ie_saude_ocupacional%type default null, dt_atendimento_p pls_conta.dt_atendimento_referencia%type default null) RETURNS PLS_TIPO_ATENDIMENTO.IE_INTERNADO%TYPE AS $body$
DECLARE

				
/* IE_TIPO_P

PORTAL - Retorna o valo pelos campos IMP. 
*/
			
ie_retorno_w			pls_tipo_atendimento.ie_internado%type;
ie_tipo_guia_w			pls_conta.ie_tipo_guia%type;
nr_seq_tipo_atend_w		pls_conta.nr_seq_tipo_atendimento%type;
cd_tiss_w			pls_tipo_atendimento.cd_tiss%type;
ie_tipo_atend_imp_w 		pls_conta.ie_tipo_atendimento_imp%type;
ie_regime_atendimento_w		pls_conta.ie_regime_atendimento%type;
ie_saude_ocupacional_w		pls_conta.ie_saude_ocupacional%type;
dt_atendimento_w		pls_conta.dt_atendimento_referencia%type;


BEGIN

ie_retorno_w	:= 'N';

-- Este campo define se os valores foram passados ou se foi passado sa a sequencia da conta.

-- Se for passada a sequencia busca os dados,
if (ie_pela_seq_p = 'S') then

	select	coalesce(max(b.ie_tipo_guia),'0'),
		max(a.nr_seq_tipo_atend_conv),
		max(a.ie_tipo_atendimento),
		max(a.ie_regime_atendimento),
		max(a.ie_saude_ocupacional),
		max(a.dt_atendimento)
	into STRICT	ie_tipo_guia_w,
		nr_seq_tipo_atend_w,
		ie_tipo_atend_imp_w,
		ie_regime_atendimento_w,
		ie_saude_ocupacional_w,
		dt_atendimento_w
	from	pls_conta_imp a,
		pls_protocolo_conta_imp b
	where	a.nr_sequencia	= nr_seq_conta_p
	and 	a.nr_seq_protocolo = b.nr_sequencia;
-- Se nao alimenta as variaveis com os dados passados.
else
	ie_tipo_guia_w	:= ie_tipo_guia_p;
	ie_tipo_atend_imp_w := ie_tipo_atend_imp_p;
	nr_seq_tipo_atend_w := nr_seq_tipo_atend_p;
	dt_atendimento_w := dt_atendimento_p;
	ie_regime_atendimento_w := ie_regime_atendimento_p;
	ie_saude_ocupacional_w	:= ie_saude_ocupacional_p;
end if;

if (ie_tipo_p = 'PORTAL') then
	
	select	max(a.nr_sequencia)
	into STRICT	nr_seq_tipo_atend_w
	from	pls_tipo_atendimento a
	where	a.cd_estabelecimento = cd_estabelecimento_p
	and	a.ie_situacao = 'A'
	and	a.cd_tiss = ie_tipo_atend_imp_w;
end if;

-- Se for resumo de internaaao ou honorario individual entao a internaaao
if (ie_tipo_guia_w in ('5','6')) then

	ie_retorno_w	:= 'S';
	
-- Se for SP\SADT tem que ver pelo tipo de atendimento da conta.
elsif (ie_tipo_guia_w	= '4') then
	
	if (nr_seq_tipo_atend_w IS NOT NULL AND nr_seq_tipo_atend_w::text <> '') then
	
		select	coalesce(max(ie_internado), 'N')
		into STRICT	ie_retorno_w
		from	pls_tipo_atendimento
		where	nr_sequencia	= nr_seq_tipo_atend_w;
	end if;
	
	--Se for encontrado algum tipo de atendimento que aponte o atendimento como sendo internacao, entao nem

	--olha mais pelo ie_regime_atendimento e ie_saude_Ocupacional na pls_parametros_tiss. Se nao encontrar

	--nenhuma regra pelo tipo de atendimento que aponte como internacao, entao procurara na pls_parametros tambem.
	if ( ie_retorno_w <> 'S') then
	
		if (ie_regime_atendimento_w IS NOT NULL AND ie_regime_atendimento_w::text <> '') then
		
			--Restringe por ie_internado = S pois o valor default da variavel  eh N e basta achar uma regra cujo ie_internado = S que seja

			--vigente, para ser o suficiente.
			select	coalesce(max(ie_internado), 'N')
			into STRICT	ie_retorno_w
			from	pls_parametros_tiss
			where	ie_regime_atendimento = ie_regime_atendimento_w
			and 	dt_inicio_vigencia <= dt_atendimento_w
			and 	dt_fim_vigencia >= dt_atendimento_w
			and 	ie_internado = 'S'
			and 	ie_situacao = 'A';
			
		end if;		
	end if;
	
	if ( ie_retorno_w <> 'S') then
	
		--Se for encontrado algum regime de atendimento que aponte o atendimento como sendo internacao, entao nem

		--olha mais pelo  ie_saude_Ocupacional na pls_parametros_tiss. Se nao encontrar

		--nenhuma regra pelo tipo de atendimento que aponte como internacao, entao procurara 

		--por regras com campo ie_saude_ocupacional na pls_parametros tambem.
		if (ie_saude_ocupacional_w IS NOT NULL AND ie_saude_ocupacional_w::text <> '') then
		
			--Restringe por ie_internado = S pois o valor default da variavel  eh N e basta achar uma regra cujo ie_internado = S que seja

			--vigente, para ser o suficiente.
			select	coalesce(max(ie_internado), 'N')
			into STRICT	ie_retorno_w
			from	pls_parametros_tiss
			where	ie_saude_ocupacional = ie_saude_ocupacional_w
			and 	dt_inicio_vigencia <= dt_atendimento_w
			and 	dt_fim_vigencia >= dt_atendimento_w
			and 	ie_internado = 'S'
			and 	ie_situacao = 'A';
			
		end if;		
	end if;
	
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_se_internado_imp ( nr_seq_conta_p pls_conta.nr_sequencia%type, ie_tipo_p text, ie_tipo_guia_p pls_conta.ie_tipo_guia%type default null, nr_seq_tipo_atend_p pls_conta.nr_seq_tipo_atendimento%type default null, ie_tipo_atend_imp_p pls_conta.ie_tipo_atendimento_imp%type default null, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type default null, ie_pela_seq_p text default 'S', ie_regime_atendimento_p pls_conta.ie_regime_atendimento%type default null, ie_saude_ocupacional_p pls_conta.ie_saude_ocupacional%type default null, dt_atendimento_p pls_conta.dt_atendimento_referencia%type default null) FROM PUBLIC;

